const BASE_MP4_BASE64 =
  "";

const baseBuffer = Buffer.from(BASE_MP4_BASE64, "base64");
const targetSize = 2300;
const padding =
  baseBuffer.length >= targetSize
    ? Buffer.alloc(0)
    : Buffer.alloc(targetSize - baseBuffer.length);
const padded = Buffer.concat([baseBuffer, padding]);

export const SAMPLE_MP4_BASE64 = padded.toString("base64");
